import React, { useState, useEffect } from 'react'


const RecipesTable = () => {
    const [dataRows, setDataRows] = useState([])

    useEffect(() => {
        fetch('http://localhost:1337/api/recipes-table')
            .then(res => res.json())
            .then(data => setDataRows(data))
            .catch(error => console.error(error))
    }, [])

    var fieldNames = []
    if(setDataRows[0]){
        fieldNames = Object.keys(setDataRows[0])
    }
    fieldNames.shift()
    
    return (
        <table>
            <thead>
            <tr>
            {fieldNames.map(fieldName => (
                <th key={fieldName}>{fieldName}</th>
            ))}
                </tr>
            </thead>
            <tbody>
                {dataRows.map(row => (
                    <tr key={row._id}>
                    {fieldNames.map(fieldName => (
                        <td key={fieldName}>{row[fieldName]}</td>
                    ))}
                    </tr>
                ))}
            </tbody>
        </table>
    )

}

export default RecipesTable
