import React, { useEffect } from 'react';
import styles from './Loading.module.css'; // Import the CSS module

const Loading = () => {
  useEffect(() => {
    // Add a class to the body element to set the background color
    document.body.classList.add(styles.bodyBackground);
    
    // Remove the class from the body element when the Loading component is unmounted
    return () => {
      document.body.classList.remove(styles.bodyBackground);
    };
  }, []);

  return (
    <div className={styles.ring}>
      Loading
      <span></span>
    </div>
  );
};

export default Loading;
