import Navbar from '../components/Navbar';
import RecipeCard from '../components/RecipeCard';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import './App.css';
import React, { useState, useEffect } from 'react';
import categoryMap from './categoryMap';

const SearchRecipe = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const name = location.state.name;
    const [categories, setCategories] = useState([]);
    const [expandedCategories, setExpandedCategories] = useState({});
    const [recipes, setRecipes] = useState([]);
    const [checkedItems, setCheckedItems] = useState({});
    const [filteredRecipes, setFilteredRecipes] = useState([]);
    const [recipesCategories, setRecipesCategories] = useState([]);
    useEffect(() => {
        const filteredRecipeIds = [];
        Object.keys(checkedItems).forEach(category => {
            Object.keys(checkedItems[category]).forEach(value => {
                if (checkedItems[category][value]) {
                    const recipeIds = recipesCategories.find(item => item.category === category)?.recipes?.filter(recipeId => filteredRecipeIds.includes(recipeId));
                    filteredRecipeIds.push(...recipeIds);
                }
            });
        });
        setFilteredRecipes(recipes.filter(recipe => filteredRecipeIds.includes(recipe.RecipeId)));
    }, [checkedItems, recipes, recipesCategories]);

    useEffect(() => {
        fetch(`http://localhost:1337/api/table/recipes`)
            .then(res => res.json())
            .then(data => setRecipes(data))
            .catch(error => console.error(error))
    }, []);

    useEffect(() => {
        fetch(`http://localhost:1337/api/recipes_categories`)
            .then(res => res.json())
            .then(data => setRecipesCategories(data))
            .catch(error => console.error(error))
    }, []);

    console.log(recipesCategories)

    useEffect(() => {
        fetch('http://localhost:1337/api/home/search_recipe')
            .then(response => response.json())
            .then(data => {
                const expandedCategories = {};
                const checkedItems = {};
                Object.keys(data).forEach(category => {
                    expandedCategories[category] = false;
                    checkedItems[category] = {};
                });
                setCategories(data);
                setExpandedCategories(expandedCategories);
                setCheckedItems(checkedItems);
            })
            .catch(error => console.error(error));
    }, []);

    const toggleCategory = (category) => {
        setExpandedCategories({
            ...expandedCategories,
            [category]: !expandedCategories[category]
        });
    };

    function filterRecipes() {
        const selectedCategories = Object.keys(checkedItems).filter((category) => {
            console.log("check1")
            return Object.values(checkedItems[category]).some((isChecked) => isChecked);
        });

        if (selectedCategories.length === 0) {
            console.log("check2")
            return recipes;
        }

        const selectedRecipes = {};
        selectedCategories.forEach((category) => {
            Object.keys(checkedItems[category]).forEach((element) => {
                if (checkedItems[category][element]) {
                    recipesCategories[category][element].forEach((recipeId) => {
                        selectedRecipes[recipeId] = true;
                    });
                }
            });
        });

        return recipes.filter((recipe) => {
            return selectedRecipes[recipe.RecipeId];
        });
    }

    const handleCheckboxChange = (category, value) => {
        const checkedItemsCopy = { ...checkedItems };
        checkedItemsCopy[category][value] = !checkedItemsCopy[category][value];
        setCheckedItems(checkedItemsCopy);

        const filteredRecipes = filterRecipes();
        setFilteredRecipes(filteredRecipes);
    };

    // const handleCheckboxChange = (category, value) => {
    //     const checkedItemsCopy = { ...checkedItems };
    //     checkedItemsCopy[category][value] = !checkedItemsCopy[category][value];
    //     setCheckedItems(checkedItemsCopy);
    // };

    const handleClick = (recipeId) => {
        navigate(`/recipes/${recipeId}`);
    };


    return (
        <div>
            {name && <Navbar name={name} />}
            <div className='search-recipe-container'>
                <div className='filter-menu'>
                    {Object.keys(categories)
                        .sort((a, b) => a.localeCompare(b))
                        .map((category) => (
                            <div className='category' key={category}>
                                <div className="category-header" onClick={() => toggleCategory(category)}>
                                    <span className='category-title'>{category}</span>
                                    <button className="btn btn-light category-toggle-btn">{expandedCategories[category] ? "-" : "+"}</button>
                                </div>
                                {expandedCategories[category] && categories[category].sort().map((value) => (
                                    <div className="form-check" key={value}>
                                        <input className="form-check-input" type="checkbox" id={`checkbox_${value}`} checked={checkedItems[category][value]} onChange={() => handleCheckboxChange(category, value)} />
                                        <label className="form-check-label" htmlFor={`checkbox_${value}`}>{value}</label>
                                    </div>
                                ))}
                            </div>
                        ))}
                </div>
                <div className='recipes-container'>
                    {filteredRecipes.map((recipe) => (
                        <div key={recipe.RecipeId} onClick={() => handleClick(recipe.RecipeId)}>
                            <RecipeCard recipe={recipe} />
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default SearchRecipe
