import './RecipeCard.css';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import LikeButton from './LikeBtn';


const defaultImageUrl = '/images/logo-image.png'

const RecipeCard = (props) => {
  const [imageUrl, setImageUrl] = useState(null);
  const recipe = props.recipe;
  const user = props.user;

  const name = user.name
  const user_id = user.email
  const navigate = useNavigate();


  useEffect(() => {
    async function getImageUrl() {
      const response = await fetch(`http://localhost:1337/api/recipes/images/${recipe.RecipeId}`);
      const data = await response.text();
      if (data !== 'Image not found' || data !== 'Error fetching image') {
        //setImageUrl(data)
        const resp = await fetch(data);
        if (resp.ok) {
          // console.log("if1")
          setImageUrl(data) // URL is working, send the URL as a response
        } else {
          // console.log("if2")
          setImageUrl(defaultImageUrl)
        }
      }
      else {
        // console.log("if3")
        setImageUrl(defaultImageUrl)
      }
    }
    getImageUrl();
  }, [recipe.RecipeId]);

  const handleClick = (recipeId) => {
    navigate(`/recipes/${recipeId}`, { state: { name: name, user_id: user_id } });
  };


  return (
    <div className="card recipe-card">
      {imageUrl && <img className="card-img-top" src={imageUrl} alt="Card cap"></img>}
      <div className="card-body">
        <div key={recipe.RecipeId} onClick={() => handleClick(recipe.RecipeId)}>
          <h6 className="  /* recipe card */

          .recipe-card {
            display: flex;
            flex-direction: column;
            width: 19em;
            height: 25em; /* Increase the height to fit the larger image */
            text-align: center;
            align-items: center;
            border-radius: 15px;    
          }
        
          .card.recipe-card {
            box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); /* Adjust values as needed */
          }
          
          .card-img-top {
            flex: 1; /* Allows the image to take up the full available space */
            object-fit: cover; /* Ensures the image fills the available space */
            border-top-left-radius: 15px; /* Adds rounded corners to the top of the image */
            border-top-right-radius: 15px;
            height: 50%;
          }
        
          .card-body {
            flex: 2; /* Allows the card body to take up the remaining space */
            text-align: left; /* Aligns the card content to the left */
            height: auto;
          }
        
          .card-text {
            text-transform: capitalize;
            max-height: 8em; /* Change the height value to your desired height */
            overflow: hidden;
            text-overflow: ellipsis;
            display: -webkit-box;
            -webkit-line-clamp: 2; /* Change the line clamp value to the number of lines you want to show */
            -webkit-box-orient: vertical;
            transition: max-height 0.5s ease-out; /* Add a smooth transition effect */
            font-size: 14px; 
          }
        
          .card-text:hover {
            max-height: none; /* Remove the max-height limit on hover */
          }
          
          .card-title{
            text-transform: capitalize;
            color: #4D4D4C;
            cursor: pointer;
          }
        
        
        .like-wrapper {
          display: inline-flex;
          align-items: center;
          padding: 8px;
          background-color: transparent;
          border: 2px solid orange;
          border-radius: 10px;
          cursor: pointer;
          width: fit-content;
          margin-left: 5px;
        }
        
        .like-text {
          margin-left: 8px;
          color: orange;
          font-size: 25px;
        }
        
        /* Style for the heart icon */
        .like-wrapper i {
          fill: currentColor;
          margin-left: 15px;
          margin-right: 8px;
          font-size: 25px;
        }
        
        .like-wrapper .bi-heart {
          color: orange;
        }
        
        .like-wrapper .bi-heart-fill {
          color: orange;
        }
        
        .heart-icon{
          width: 16px;
          height: 16px;
        }
        
          .bi-heart {
            color: #000000;
          }
        
          .bi-heart-fill{
            color: #e73737;
          }
          .nevigate:hover{
            cursor: pointer;
          }
          .heart:hover{
            cursor: pointer;
          }">{recipe.Name}</h6>
        </div>
        <p className="card-text">{recipe.Description}</p>
        <div>
          <LikeButton recipeId={recipe.RecipeId} userEmail={user.email} pageType="RecipeCard" />
        </div>
      </div>
    </div>
  );
};

export default RecipeCard
