        const data = await response.json();
        setFavoritesRecipes(data);
        console.log(favoritesRecipes)
    } catch (error) {
        console.error('Error fetching favorite recipes:', error);
    }
};


  return (
    <div>
      <h2>Recommended Recipes</h2>
      <ul>
        {recommendations.map(recipe => (
          <li key={recipe.RecipeId}>
            {recipe.recipe.Name} ({recipe.recipe.RecipeCategory})
          </li>
        ))}
      </ul>

      <h2>Favorite Recipes</h2>
      <ul>
        {favoritesRecipes.map(recipe => (
          <div id="fav-card" className='recipe-card-wrapper'>
            <RecipeCard recipe={recipe} user={user} onLikeToggle={handleLikeToggle} />
          </div>
        ))}
      </ul>

    </div>
  );
};

export default Recommendations;
