import React from 'react';

function StarRating({ rating }) {
  const maxRating = 5; // Total number of stars
  const filledStars = Math.floor(rating); // Number of filled stars
  const unfilledStars = maxRating - filledStars; // Number of unfilled stars

  // Create an array of star elements
  const stars = [];


  for (let i = 0; i < filledStars; i++) {
    stars.push(
      <span key={i} className="star filled-star">
        ★
      </span>
    );
  }

  for (let i = 0; i < unfilledStars; i++) {
    stars.push(
      <span key={i + filledStars} className="star unfilled-star">
        ☆
      </span>
    );
  }

  return <div className="rating-stars">{stars}</div>;
}

export default StarRating;
