import Navbar from '../components/Navbar';
import './App.css';
import './Groceries.css'
import './AddRecipe.css'
import React, { useState, useEffect } from 'react';
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom'
import './Connect.css';

const Groceries = () => {
  const navigate = useNavigate()
  const [name, setName] = useState(null)
  const [user, setUser] = useState(null)
  const [ingredients, setIngredients] = useState([])
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredIngredients, setFilteredIngredients] = useState([]);
  const [selectedIngredient, setSelectedIngredient] = useState({});
  const [groceryList, setGroceryList] = useState([]);
  const [recipeIngredients, setRecipeIngredients] = useState([]);


  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      const _user = jwt_decode(token)
      setName(_user.name)
      setUser(_user)
      if (!_user) {
        localStorage.removeItem('token')
        navigate.replace('/login')
      }
    }
  }, [navigate])

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:1337/api/groceries`)
        .then(res => res.json())
        .then(data => {
          setIngredients(data)
        })
        .catch(error => console.error(error))
    }
  }, [user]);

  useEffect(() => {
    if (searchTerm.length >= 3) {
      const filtered = ingredients.filter((ingred) =>
        ingred && ingred.ingredient
          ? ingred.ingredient.toLowerCase().includes(searchTerm.toLowerCase())
          : false
      );
      setFilteredIngredients(filtered);
    } else {
      setFilteredIngredients([]);
    }
  }, [searchTerm, ingredients]);

  useEffect(() => {
    // Load grocery list from local storage when the component mounts
    const storedGroceryList = JSON.parse(localStorage.getItem('groceryList')) || [];
    setGroceryList(storedGroceryList);
  }, []);

  useEffect(() => {
    // Save grocery list to local storage whenever it changes
    localStorage.setItem('groceryList', JSON.stringify(groceryList));
  }, [groceryList]);

  const handleAddToGroceryList = () => {
    if (selectedIngredient) {
      const newItem = {
        id: selectedIngredient.id,
        ingredient: selectedIngredient.ingredient,
      };
      setGroceryList([...groceryList, newItem]);
      // Clear the input fields after adding to the list
      setSelectedIngredient({});
      setSearchTerm('');
    }
  };

  const handleDeleteFromGroceryList = (id) => {
    const updatedList = groceryList.filter((item) => item.id !== id);
    setGroceryList(updatedList);
  };

  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...recipeIngredients];
    updatedIngredients[index][field] = value;
    setRecipeIngredients(updatedIngredients);
    console.log(updatedIngredients)
    // Call a function to filter and update the suggestions based on the current input value
    updateIngredientSuggestions(value, index);
  };

  const updateIngredientSuggestions = (inputValue, index) => {
    // Check if the length of inputValue is at least 3 before searching for suggestions
    if (inputValue.length >= 3) {
      const filteredIngredients = ingredients
        .filter((ingredient) =>
          typeof ingredient.ingredient === 'string' &&
          ingredient.ingredient.toLowerCase().startsWith(inputValue.toLowerCase())
        )
        .map((ingredient) => ingredient.ingredient);

      // Update the suggestions array at the specified index
      setSuggestions((prevSuggestions) => {
        const updatedSuggestions = [...prevSuggestions];
        updatedSuggestions[index] = filteredIngredients;
        return updatedSuggestions;
      });
    } else {
      // Clear suggestions if inputValue is less than 3 letters
      setSuggestions((prevSuggestions) => {
        const updatedSuggestions = [...prevSuggestions];
        updatedSuggestions[index] = [];
        return updatedSuggestions;
      });
    }
  };

  const handleSuggestionClick = (index, suggestion) => {
    const updatedIngredients = [...recipeIngredients];

    updatedIngredients[index].ingredient = suggestion;
    setRecipeIngredients(updatedIngredients);
    // Clear suggestions for the clicked input field
    setSuggestions([], index);
  };

  return (
    <div>
      {name && <Navbar name={name} />}
      <div className='ing-page-main-container'>

        <div className='ing-page-head'>
          <div className='ing-page-main-title'>
            Discover Recipes with What
            <br />
            You Have
          </div>
          <div className='ing-page-sub-title'>
            Cook delicious meals using ingredients you already have at home
          </div>


          <div className='ing-page-search-container'>

            <div className="input-container">
              <input
                id={`ingredient_name`}
                className='input-field ingredient-input'
                placeholder={`Ingredient`}
                value={ingredient.ingredient}
                onChange={(e) => handleIngredientChange('ingredient', e.target.value)}
                required
              />

            </div>

            {/*
            <div className="custom-dropdown">
              <input
                className='select-ingredients'
                type="text"
                placeholder="Add ingredient"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <button onClick={handleAddToGroceryList} className='add-btn-add-recipe'>
              <i className="bi bi-plus-circle add-icon"></i>
            </button>
             */}

          </div>

          {searchTerm.length >= 3 && (
            <div className="dropdown-ingredient">
              {filteredIngredients.map((ingredient) => (
                <div
                  key={ingredient.id}
                  className="dropdown-item"
                  onClick={() => {
                    setSearchTerm(ingredient.ingredient);
                    setSelectedIngredient({
                      id: ingredient.id,
                      ingredient: ingredient.ingredient,
                    });
                  }}
                >
                  {ingredient.ingredient}
                </div>
              ))}
            </div>
          )}
        </div>

        <div className='ing-page-bottom'>

          <div className='groceries-list'>
            <div className='black-title'>Grocery List</div>
            <ul>
              {groceryList.map((item, index) => (
                <li key={index}>
                  {item.ingredient}
                  <button onClick={() => handleDeleteFromGroceryList(item.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>

        </div>


      </div>
    </div>
  );
};

export default Groceries
