// Your main file

import React, { useState } from 'react';
import AddRecipeValidation from './AddRecipeValidation';

const YourMainComponent = () => {
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [formValid, setFormValid] = useState(false);

  const handleCloseAlert = () => {
    // Perform any actions you want when the alert is closed
    // For example, reset the formSubmitted state
    setFormSubmitted(false);
  };

  // Your form logic here

  return (
    <div>
      {name && <Navbar name={name} />}
      <div>
        {user && (
          <form className="add-recipe-form needs-validation">
            {/* Other form elements go here */}
            
            {/* Include the new component */}
            <AddRecipeValidation formSubmitted={formSubmitted} formValid={formValid} onClose={handleCloseAlert} />
          </form>
        )}
      </div>
    </div>
  );
};

export default YourMainComponent;
