import Navbar from '../components/Navbar';
import './App.css';
import './Groceries.css'
import './AddRecipe.css'
import React, { useState, useEffect } from 'react';
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom'
import './Connect.css';

const Groceries = () => {
  const navigate = useNavigate()
  const [name, setName] = useState(null)
  const [user, setUser] = useState(null)
  const [ingredients, setIngredients] = useState([])
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestionsIngredients, setSuggestionsIngredients] = useState([]);
  const [selectedIngredient, setSelectedIngredient] = useState({});
  const [groceryList, setGroceryList] = useState([]);
  const [isSuggestionClicked, setIsSuggestionClicked] = useState(false);



  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      const _user = jwt_decode(token)
      setName(_user.name)
      setUser(_user)
      if (!_user) {
        localStorage.removeItem('token')
        navigate.replace('/login')
      }
    }
  }, [navigate])

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:1337/api/groceries`)
        .then(res => res.json())
        .then(data => {
          setIngredients(data)
        })
        .catch(error => console.error(error))
    }
  }, [user]);

  useEffect(() => {
    if (searchTerm.length >= 3 && !isSuggestionClicked) {
      const filtered = ingredients.filter((ingred) =>
        ingred.ingredient.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setSuggestionsIngredients(filtered);
    } else {
      setSuggestionsIngredients([]);
    }
    setIsSuggestionClicked(false); // Reset the flag
  }, [searchTerm, ingredients, isSuggestionClicked]);

  const handleSuggestionClick = (ingredient) => {
    setIsSuggestionClicked(true); // Set the flag to true
    setSearchTerm(ingredient.ingredient);
    setSelectedIngredient({
      id: ingredient.id,
      ingredient: ingredient.ingredient,
    });
    setSuggestionsIngredients([]); // Clear suggestions

  };


  const handleAddToGroceryList = () => {
    if (selectedIngredient) {
      const newItem = {
        id: selectedIngredient.id,
        ingredient: selectedIngredient.ingredient,
      };
      setGroceryList([...groceryList, newItem]);
      // Clear the input fields after adding to the list
      setSelectedIngredient({});
      setSearchTerm('');
    }
  };

  useEffect(() => {
    // Load grocery list from local storage when the component mounts
    const storedGroceryList = JSON.parse(localStorage.getItem('groceryList')) || [];
    setGroceryList(storedGroceryList);
  }, []);

  useEffect(() => {
    // Save grocery list to local storage whenever it changes
    localStorage.setItem('groceryList', JSON.stringify(groceryList));
  }, [groceryList]);



  const handleDeleteFromGroceryList = (id) => {
    const updatedList = groceryList.filter((item) => item.id !== id);
    setGroceryList(updatedList);
  };



  return (
    <div>
      {name && <Navbar name={name} />}
      <div className='ing-page-main-container'>

        <div className='ing-page-head'>
          <div className='ing-page-main-title'>
            Discover Recipes with What
            <br />
            You Have
          </div>
          <div className='ing-page-sub-title'>
            Cook delicious meals using ingredients you already have at home
          </div>


          <div className='ing-page-search-container'>

            <div className="search-ing-input-container">

              {/* 
              <input
                className='input-field grocery-input'
                type="text"
                placeholder="Add ingredient"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />

              {searchTerm.length >= 3 && (
                <div className="ingredient-suggestions">
                  <div className='toggle-bar'>
                    {suggestionsIngredients.map((ingredient) => (
                      <div
                        key={ingredient.id}
                        onClick={() => handleSuggestionClick(ingredient)}
                      >
                        {ingredient.ingredient}
                      </div>

                    ))}
                  </div>
                </div>
              )}
              */}

            </div>

            <button onClick={handleAddToGroceryList} className='add-btn-grocery'>
              <i className="bi bi-plus-circle add-icon"></i>
            </button>

          </div>

        </div>

        <div className='ing-page-bottom'>

          <div className='groceries-list-container'>
            <div className='black-title'>Grocery List</div>

            <div className='grocery-list'>
              {groceryList.map((item, index) => (

                <span key={index} className='ing-container'>
                  <label className='ing-rec'>
                    {item.ingredient}
                  </label>
                  <i
                    className='bi bi-x-circle remove-icon remove-ing'
                    onClick={() => handleDeleteFromGroceryList(item.id)}
                  ></i>
                </span>

              )
              )}

            </div>

            {/*
            <ul>
              {groceryList.map((item, index) => (
                <li key={index}>
                  {item.ingredient}
                  <button onClick={() => handleDeleteFromGroceryList(item.id)}>Delete</button>
                </li>
              ))}
            </ul>
            */}

          </div>

        </div>


        <div className='steps-container'>

          {recipeIngredients.length === 0 ? (
            // Error message when instructions array is empty
            <div className='error-message-empty'>Please add at least one ingredient.</div>
          ) : (
            // Render instructions container with inputs
            <>
              {recipeIngredients.map((ingredient, index) => (
                <div key={index} className='instruction-row'>

                  <div className="input-container">
                    <input
                      id={`ingredient_${index}_name`}
                      className='input-field ingredient-input'
                      placeholder={`Ingredient ${index + 1}`}
                      value={ingredient.ingredient}
                      onChange={(e) => handleIngredientChange(index, 'ingredient', e.target.value)}
                      required
                    />

                    {Array.isArray(suggestions[index]) && suggestions[index].length > 0 && (
                      <div className='ingredient-suggestions'>
                        <div className='toggle-bar'>
                          <ul>
                            {suggestions[index].map((suggestion, suggestionIndex) => (
                              <li key={suggestionIndex} onClick={() => handleSuggestionClick(index, suggestion)}>
                                {suggestion}
                              </li>
                            ))}
                          </ul>
                        </div>
                      </div>
                    )}

                  </div>

                  <input
                    id={`ingredient_${index}_amount`}
                    type="number"
                    className='input-field step-input amount-input'
                    placeholder={`Amount ${index + 1}`}
                    value={ingredient.amount}
                    onChange={(e) => handleIngredientChange(index, 'amount', e.target.value)}
                    required
                  />

                  <select
                    id={`ingredient_${index}_measurement`}
                    className='input-field step-input measure-input selectWithScrollbar'
                    value={ingredient.measurementId || ''}
                    onChange={(e) => handleMeasurementChange(index, e.target.value)}
                    required
                  >
                    <option value='' disabled>{`Measurement ${index + 1}`}</option>
                    {measurements.map((measurement, measurementIndex) => (
                      <option key={measurementIndex} value={measurement.measurement_id}>
                        {measurement.measurement}
                      </option>
                    ))}
                  </select>

                  <i
                    onClick={() => removeIngredient(index)}
                    className='bi bi-x-circle remove-icon'
                    title='Remove Ingredient'
                  ></i>
                </div>
              ))}

            </>
          )}
          <button onClick={addIngredient} className='add-btn-add-recipe'>
            <i className="bi bi-plus-circle add-icon"></i>
            <span>Add another ingredient</span>
          </button>

        </div>
      </div>


    </div>
  );
};

export default Groceries
