import React, { useState } from 'react';
import './AddToListModal.css';

const AddToListModal = ({ showModal, onClose, ingredients }) => {
  const [selectedIngredients, setSelectedIngredients] = useState([]);

  const handleCheckboxChange = (ingredient) => {
    setSelectedIngredients((prevSelected) =>
      prevSelected.includes(ingredient)
        ? prevSelected.filter((i) => i !== ingredient)
        : [...prevSelected, ingredient]
    );
  };

  const handleSubmit = async () => {
    // Send selectedIngredients to the server
    try {
      const response = await fetch('/api/shopping-list', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ingredients: selectedIngredients }),
      });

      if (response.ok) {
        console.log('Ingredients added to shopping list');
        onClose();
      } else {
        console.error('Failed to add ingredients');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };


  {console.log("ingredients:")}

  return (
    <div className={`center-modal modal ${showModal ? 'show' : ''}`} tabIndex="-1">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Add Ingredients to Shopping List</h5>
            <button type="button" className="btn-close" aria-label="Close" onClick={onClose}></button>
          </div>
          <div className="modal-body">
            {/*
            <form>
              {ingredients.map((ingredient) => (
                <div key={ingredient.id} className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id={ingredient}
                    value={ingredient}
                    checked={selectedIngredients.includes(ingredient)}
                    onChange={() => handleCheckboxChange(ingredient)}
                  />
                  <label className="form-check-label" htmlFor={ingredient}>
                    {ingredient}
                  </label>
                </div>
              ))}
            </form>
            */}
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Close
            </button>
            <button type="button" className="btn btn-primary" onClick={handleSubmit}>
              Add to Shopping List
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddToListModal;
