import Navbar from '../components/Navbar';
import './Shopping.css';
import React, { useState, useEffect } from 'react';
import jwt_decode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns'; // Importing format function from date-fns

const Shopping = () => {
    const navigate = useNavigate();
    const [name, setName] = useState(null);
    // eslint-disable-next-line
    const [user, setUser] = useState(null);
    const [shoppingList, setShoppingList] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            const _user = jwt_decode(token);
            setName(_user.name);
            setUser(_user);
            if (!_user) {
                localStorage.removeItem('token');
                navigate.replace('/login');
            }
        }

        // Retrieve shopping list from local storage
        const storedList = JSON.parse(localStorage.getItem('shoppingList')) || [];
        setShoppingList(storedList);
    }, [navigate]);

    return (
        <div>
            {name && <Navbar name={name} />}
            <div className="shopping-list">
                <h2>{name ? `${name}'s` : 'Your'} Shopping List</h2>
                <ul>
                    {shoppingList.length > 0 ? (
                        shoppingList.map((ingredient, index) => (
                            <li key={index} className="shopping-list-item">
                                <div className="shopping-list-item-name">{ingredient.name}</div>
                                <div className="shopping-list-item-date">
                                    Added on {format(new Date(ingredient.addedDate), 'MMMM dd, yyyy')}
                                </div>
                            </li>
                        ))
                    ) : (
                        <li className="shopping-list-item">Your shopping list is empty.</li>
                    )}
                </ul>
            </div>
        </div>
    );
};

export default Shopping;
