import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Components.css';

const LogoutBtn = ({ userName }) => {
    const navigate = useNavigate();

    const token = localStorage.getItem('token');

    const logOut = (event) => {
        event.preventDefault();
        localStorage.clear();
        navigate('/');
    };

    const handleClick = (event) => {
        console.log("logoutBTN")
        if (event.target.closest('svg, input')) {
            event.preventDefault();
            logOut(event);
        }
    };


    console.log("logoutBTN")
    
    return (
        <div className='dropdown-menu-button' onClick={handleClick}>
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-door-open icon" viewBox="0 0 16 16">
                <path d="M8.5 10c-.276 0-.5-.448-.5-1s.224-1 .5-1 .5.448.5 1-.224 1-.5 1z" />
                <path d="M10.828.122A.5.5 0 0 1 11 .5V1h.5A1.5 1.5 0 0 1 13 2.5V15h1.5a.5.5 0 0 1 0 1h-13a.5.5 0 0 1 0-1H3V1.5a.5.5 0 0 1 .43-.495l7-1a.5.5 0 0 1 .398.117zM11.5 2H11v13h1V2.5a.5.5 0 0 0-.5-.5zM4 1.934V15h6V1.077l-6 .857z" />
            </svg>
            <input className='dropdown-menu-text' type="submit" value={!token && userName === 'Guest' ? 'Sign In' : 'Log Out'} />
        </div>
    );
};

export default LogoutBtn;
